import React, { useState, useEffect } from 'react'
import axios from 'axios'
import md5 from 'md5'

const Detail = props => {
  console.log('Detail -> props', props.match.params.id)
  const [detail, setDetail] = useState({})
  useEffect(() => {
    const privateKey = 'cc910b666eec23762ab78c8ca89332202ae580b8'
    const publicKey = 'd1925cd3b431b62ac236c00d3c681015'
    const timeStamp = new Date().getMilliseconds()
    const hash = md5(`${timeStamp}${privateKey}${publicKey}`)
    const generateUrl = `https://gateway.marvel.com/v1/public/characters/${props.match.paramas.id}`

    axios({
      method: 'GET',
      url: generateUrl,
      params: {
        ts: timeStamp,
        apikey: publicKey,
        hash: hash
      }
    })
      .then(res => {
        console.log(res)
        setDetail(res.data.data.results[0])
      })
      .catch(err => {
        console.log(err)
      })
  }, [])

  return (
    <div>
      <p>detail</p>
      <p>{detail.name}</p>
      <p>{detail.description}</p>
    </div>
  )
}

export default Detail
